public class Task71 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Primitive data types : int,double,char.boolean,float,byte,short,long
				//1.int
				int i=0;
				int a=1;
				System.out.println(i+a);
				int b=-1;
				System.out.println(b);
				
				//2.double
				double t= 11.2;
				double g= 22.0;
				double e= 100; //100.00
				System.out.println(e);
				
				//3.char char should in single digit value and should be in single quote
				char b2= 'a';
				char b1= 'A';
				char b3='1';
				char b4='$';
				System.out.println(b4);
				
				//4.Boolean
				boolean c1= true;
				boolean c2= false;
				System.out.println(c2);
				
				//5.float
				float f1=3.14f;
				System.out.println(f1);
				
				//6.byte
				
				byte b11=100;
				System.out.println(b11);
				
				//7.short
				
				short s=20000;
				System.out.println(s);
				
				//8.long
				
				long l=123456789012345L;
				System.out.println(l);
				
				
				
				
				


	}

}
