package tasks1;

public class Task91 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int d1=20;
		int d2=30;
		int d3 =40;
		
		//nested if else
		if(d1>d2 && d1>d3) { //false false = false
			System.out.println("d1 is greater");
		}
		else if(d2>d3) {
			System.out.println("d2 is greater");
		}
		else {
			System.out.println("d3 is larger");
		}


	}

}
